import React, { useState } from "react";
import styled from "styled-components";
import { motion } from "framer-motion";
import { ISourceOptions } from "tsparticles-engine";
import Particles from "react-tsparticles";
import GridCell from "./GridCell";
import { Mahjong } from "./game/mahjongs/Mahjong";

type GridProps = {
  columns: number;
  rows: number;
  gap: number;
  height: number;
  width: number;
  style?: React.CSSProperties;
  mahjongs: (Mahjong | null)[][];
};

const StyledGrid = styled(motion.div)<GridProps>`
  position: relative;
  bottom: ${({ height, gap, rows }) => rows * (height + gap)}px;
  right: ${({ width, gap, columns }) => columns * (width + gap)}px;
`;

const isBlocked = (
  index: number,
  columns: number,
  rows: number,
  mahjongs: (Mahjong | null)[][]
): boolean => {
  const row = Math.floor(index / columns);
  const col = index % columns;
  if (mahjongs[row][col] === null) {
    return true;
  }
  return false;
};

const Grid: React.FC<GridProps> = ({
  columns,
  rows,
  gap,
  height,
  width,
  style,
  mahjongs,
}) => {
  const [selectedCells, setSelectedCells] = useState<{ [key: number]: boolean }>(
    {}
  );
  const [triggerParticles, setTriggerParticles] = useState(false);
  const [particlePosition, setParticlePosition] = useState({ x: 0, y: 0 });

  const onCellClick = (index: number) => {
    const currentlySelectedCells = Object.keys(selectedCells).filter(
      (key) => selectedCells[+key]
    );
    if (currentlySelectedCells.length === 1) {
      const cell1Index = +currentlySelectedCells[0];
      const cell2Index = index;
      if (cell1Index === cell2Index) {
        setSelectedCells({});
        return;
      }
      const cell1 =
        mahjongs[Math.floor(cell1Index / columns)][cell1Index % columns];
      const cell2 =
        mahjongs[Math.floor(cell2Index / columns)][cell2Index % columns];
      if (cell1?.image === cell2?.image) {
        console.log("Match found:", cell1, cell2);
        mahjongs[Math.floor(cell1Index / columns)][
          cell1Index % columns
        ] = null;
        mahjongs[Math.floor(cell2Index / columns)][
          cell2Index % columns
        ] = null;

        // Trigger particles at the matching cell position
        setTriggerParticles(true);
        const row = Math.floor(cell2Index / columns);
        const col = cell2Index % columns;
        setParticlePosition({
          x: col * (width + gap),
          y: row * (height + gap),
        });

        // Stop particles after 1 second
        setTimeout(() => setTriggerParticles(false), 1000);
      }
      setSelectedCells({});
    } else {
      setSelectedCells((prevSelectedCells) => ({
        ...prevSelectedCells,
        [index]: !prevSelectedCells[index],
      }));
    }
  };

  const particleOptions: ISourceOptions = {
    fullScreen: { enable: false },
    particles: {
      number: { value: 30 }, // Reduced for better performance
      color: { value: ["#FF0000", "#00FF00", "#0000FF"] }, // Multi-color
      shape: { type: ["circle", "triangle", "square"] }, // Different shapes
      size: { value: { min: 2, max: 10 } },
      move: {
        enable: true,
        speed: 10,
        direction: "none",
        random: true,
        outModes: { default: "destroy" },
      },
    },
    detectRetina: true,
  };

  return (
    <>
      <StyledGrid
        columns={columns}
        rows={rows}
        gap={gap}
        height={height / 2}
        width={width / 2}
        style={style}
        mahjongs={mahjongs}
      >
        {mahjongs.flat().map((mahjong, index) => (
          <GridCell
            key={index}
            isBlocked={isBlocked(index, columns, rows, mahjongs)}
            column={index % columns}
            row={Math.floor(index / columns)}
            gap={gap}
            height={height}
            width={width}
            isSelected={selectedCells[index] || false}
            onClick={() => onCellClick(index)}
            mahjong={mahjong}
          />
        ))}
      </StyledGrid>
      {triggerParticles && (
        <div
          style={{
            position: "absolute",
            top: particlePosition.y,
            left: particlePosition.x,
            width: width,
            height: height,
            pointerEvents: "none",
            zIndex: 100,
          }}
        >
          <Particles options={particleOptions} />
        </div>
      )}
    </>
  );
};

export default Grid;
