import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import Grid from './Grid';
import { Level } from './game/Level';
import { Mahjong } from './game/mahjongs/Mahjong';
import {GridCells} from "./GridCell";
const LayerContainer = styled(motion.div)<{level: Level}>`
  position: absolute;
  width: 100%;
  height: 100vh;
  background-color: #41454C;

`;
const isLeftRightNeighbour = (index: number, columns: number, rows: number, mahjongs: (Mahjong | null)[][]) => {
    const leftIndex = index - 1;
    const rightIndex = index + 1;
    const isLeftNeighbour = leftIndex >= 0 && leftIndex % columns !== 0 && mahjongs[Math.floor(leftIndex / columns)][leftIndex % columns];
    const isRightNeighbour = rightIndex < columns * rows && rightIndex % columns !== 0 && mahjongs[Math.floor(rightIndex / columns)][rightIndex % columns];
    const isLeftBounded = index % columns === 0;
    const isRightBounded = (index + 1) % columns === 0;
    return (!isLeftNeighbour || isLeftBounded) || (!isRightNeighbour || isRightBounded);
  };
type MultiLayerGridProps = {
  level: Level;
};
const MultiLayerGrid: React.FC<MultiLayerGridProps> = ({ level }) => {
  const cells = level.board.flatMap((layer) => layer);

  return (
    <LayerContainer level={level}>
      {cells.map((cell, index) => (
        <GridCell
          key={index}
          isBlocked={!isLeftRightNeighbour(index, level.size.x, level.size.y, cells)}
          column={index % level.size.x}
          row={Math.floor(index / level.size.x)}
          gap={level.size.gap}
          height={level.size.height}
          width={level.size.width}
          isSelected={selectedCells[index] || false}
          onClick={() => onCellClick(index)}
          mahjong={cell}
        />
      ))}
    </LayerContainer>
  );
};

export default MultiLayerGrid;
