import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Mahjong } from './game/mahjongs/Mahjong';

type GridCellProps = {
  isSelected: boolean;
  onClick: () => void;
  mahjong: (Mahjong | null);
  column: number;
  row: number;
  gap: number;
  height: number;
  width: number;
};

const StyledGridCell = styled(motion.div)<{ row: number; column: number; width: number; height: number; gap: number; isSelected: boolean; mahjong: (Mahjong | null);}>`
  background-image: ${({mahjong}) => `url(${mahjong ? mahjong.image : ''})`};
  background-size: cover;
  background-position: center;
  cursor: grab;
  top: ${({ row, height, gap }) => row * (height + gap)}px;
  left: ${({ column, width, gap }) => column * (width + gap)}px;
  width: ${({ width }) => width}px;
  height: ${({ height }) => height}px;
  ${({ mahjong }) => !mahjong && 'pointer-events: none;'}
  filter: ${({ isSelected }) => isSelected ? 'grayscale(1) contrast(0.5) sepia(1) hue-rotate(5deg)' : 'none'};
`;

const GridCell: React.FC<GridCellProps> = ({column, row, gap, height, width, isSelected, onClick, mahjong }) => {
  return (
    <StyledGridCell
      onClick={onClick}
      isSelected={isSelected}
      mahjong={mahjong}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      whileDrag={{ scale: 0.95 }}
      column = {column}
      row = {row}
      gap = {gap}
      height={height}
      width={width}
      drag
      dragConstraints={{ top: 0, left: 0, right: 0, bottom: 0 }}
    />
  );
};

export default GridCell;
