import React from 'react'
import styled, { css } from 'styled-components'
import { motion } from 'framer-motion'

type GridProps = {
  columns: number
  rows: number
  gap: number
  height: number
  width: number
}

const StyledGrid = styled(motion.div)<GridProps>`
  display: grid;
  ${({ columns, rows, gap, height, width }) => css`
    grid-template-columns: repeat(${columns}, ${width}px);
    grid-template-rows: repeat(${rows}, ${height}px);
    grid-gap: ${gap}px;
    justify-content: center;
    align-content: center;
    background-color: #282c34;
    height: 100vh;
  `}
`

const GridCell = styled(motion.div)`
  background-color: red;
  whileHover={{ scale: 1.1 }}
  whileTap={{ scale: 0.95 }}
  whileDrag={{ scale: 0.9, rotate: 10 }}
  dragConstraints={{top: 0, left: 0, right: 0, bottom: 0}}

`

const Grid = ({ columns, rows, gap, height, width}: GridProps) => {
  return (
    <StyledGrid columns={columns} rows={rows} gap={gap} height={height} width={100}>
      {[...Array(columns * rows)].map((_, index) => (
        <GridCell key={index} drag dragConstraints={{top: 0, left: 0, right: 0, bottom: 0}}/>
      ))}
    </StyledGrid>
  )
}

export default Grid
