import { motion } from 'framer-motion';
import styled from 'styled-components';
import { DefaultClause } from 'typescript';

type Draggable = {
    howerscale?: number;
    tapscale?: number;
    dragscale?: number;
};

const defaultDraggable: Draggable = {
    howerscale: 0.95,
    tapscale: 0.95,
    dragscale: 0.95,
};

const DraggableComponent = styled(motion.div)<Draggable>`
whileHover={{ scale: 1.05 }} 
whileTap={{ scale: 0.95 }} 
whileDrag={{ scale: 0.95 }} {{ scale: ${({ dragscale }) => dragscale }}}
drag
    dragConstraints={{ top: 0, left: 0, right: 0, bottom: 0 }}
`;

export default DraggableComponent;
