import React from 'react'
import styled, { css } from 'styled-components'

const StyledGrid = styled.div`
  display: grid;
  ${props => css`
    grid-template-columns: repeat(${props.cols}, ${props.cellWidth}px);
    grid-template-rows: repeat(${props.rows}, ${props.cellHeight}px);
    grid-gap: ${props.gap}px;
    justify-content: ${props.justifyContent};
    align-content: ${props.alignContent};
    background-color: ${props.backgroundColor};
    height: ${props.height};
  `}
`

const Grid = ({
  cols = 15,
  rows = 10,
  cellWidth = 100,
  cellHeight = 150,
  gap = 10,
  justifyContent = 'center',
  alignContent = 'center',
  backgroundColor = '#282c34',
  height = '100vh',
}) => {
  return (
    <StyledGrid
      cols={cols}
      rows={rows}
      cellWidth={cellWidth}
      cellHeight={cellHeight}
      gap={gap}
      justifyContent={justifyContent}
      alignContent={alignContent}
      backgroundColor={backgroundColor}
      height={height}
    >
      {[...Array(cols * rows)].map((_, index) => (
        <div key={index} className="grid-cell" style={{ backgroundColor: 'white' }}></div>
      ))}
    </StyledGrid>
  )
}

export default Grid
