import { motion } from 'framer-motion';
import styled from 'styled-components';

const StyledGridCell = styled(motion.div)`
  position: absolute;
  background: ${({ mahjong }) => (mahjong ? `url(${mahjong.image})` : 'none')};
  background-size: cover;
  background-position: center;
  width: ${({ width }) => width}px;
  height: ${({ height }) => height}px;
  top: ${({ row, height, gap }) => row * (height + gap)}px;
  left: ${({ column, width, gap }) => column * (width + gap)}px;
`;

const shakeAnimation = {
  x: [0, -10, 10, -10, 10, 0],
  transition: {
    duration: 0.5,
    times: [0, 0.2, 0.4, 0.6, 0.8, 1],
  },
};

const GridCell = ({
  mahjong,
  width,
  height,
  column,
  row,
  gap,
  animateMatch,
  onClick,
}) => {
  return (
    <StyledGridCell
      mahjong={mahjong}
      width={width}
      height={height}
      column={column}
      row={row}
      gap={gap}
      onClick={onClick}
      animate={animateMatch ? shakeAnimation : undefined}
      exit={{ opacity: 0, scale: 0 }}
    />
  );
};

export default GridCell;
