import { Mahjong, EmptyMahjong } from './mahjongs/Mahjong';

export class Level {
  id: number;
  difficulty: number;
  board: Mahjong[][][];
  background: string;

  constructor(id: number, difficulty: number, boardSize: { x: number; y: number; layers: number }, background: string) {


    this.id = id;
    this.difficulty = difficulty;
    this.background = background;

    // Initialize board with EmptyMahjong instances
    this.board = Array.from({ length: boardSize.layers }, () =>
      Array.from({ length: boardSize.y }, () =>
        Array.from({ length: boardSize.x }, () => new EmptyMahjong())
      )
    );
  }
}
